import React from 'react';

const DateDisplay: React.FC = () => {
    const date = new Date();
    const formattedDate = date.toLocaleDateString('en-US', {
        year: 'numeric',
        month: 'long',
        day: 'numeric',
    });
    const formattedTime = date.toLocaleTimeString('en-US', {
        hour: '2-digit',
        minute: '2-digit',
        hour12: false,
        timeZone: 'America/Los_Angeles',
        timeZoneName: 'short',
    });

    return (
        <div style={{ position: 'relative', padding: "", width: '100%', display: 'flex', justifyContent: 'right', alignItems: 'right', flexDirection: 'row', margin: '5px' }}>
            <span style={{ color: 'orange' }}>{formattedDate}{' '}{formattedTime}</span>
        </div>
    );
};

export default DateDisplay;
