import React from 'react';
import { useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import DateDisplay from '../components/DateDisplay';
import '../styles/_notfoundpage.sass'; // Ensure you have the correct path to your SASS file
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';

const NotFoundPage: React.FC = () => {
    const navigate = useNavigate();

    /**
     * Call this function to redirect the user to the homepage.
     */
    const redirectToHomePage = () => {
        navigate('/');
    };

    return (
        <div className="container">
            <Helmet>
                <title>Page Not Found</title>
            </Helmet>
            <Header title="Page Not Found" />
            <DateDisplay />
            <div className="content notfound-content">
                <h1>Oops 404!</h1>
                <h1>Page Not Found</h1>
                <span className="redirect-link" onClick={() => navigate('/home')}>
                    Click here to return <FontAwesomeIcon icon={faHome} />.
                </span>
            </div>
            <Footer />
        </div>
    );
};

export default NotFoundPage;
