import React, { useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Helmet } from 'react-helmet';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faHome } from '@fortawesome/free-solid-svg-icons';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import DateDisplay from '../components/DateDisplay';
import '../styles/_notfoundpage.sass'; // Ensure you have the correct path to your SASS file

const NotFoundPage: React.FC = () => {
    const navigate = useNavigate();

    useEffect(() => {
        console.log('loaded Monoton');
    }, []);

    /**
     * Call this function to redirect the user to the homepage.
     */
    const redirectToHomePage = () => {
        navigate('/');
    };

    return (
        <div className="container">
            <Helmet>
                <title>4 0 4</title>
            </Helmet>
            <Header title="Page Not Found" />
            <DateDisplay />
            <div className="content notfound-content">
                <h2>Oops 404!</h2>
                <h1>Page Not Found</h1>
                <h1 style={{ fontFamily: 'Monoton', fontSize: 50, margin: 0 }}>Super simple :)</h1>
                <p>Sorry, the page you are looking for does not exist.</p>
                <p>
                    <span className="redirect-link" onClick={redirectToHomePage}>
                        Click here to return to <FontAwesomeIcon icon={faHome} />
                    </span>
                </p>
            </div>
            <Footer />
        </div>
    );
};

export default NotFoundPage;
