import React, { useState } from 'react';
import HamburgerMenuButton from './HamburgerMenuButton';
import SideNavBar from '../SideNavBar';
import './_header.sass'; // Import the SASS file for styling

const Header: React.FC = () => {
    const [isNavOpen, setIsNavOpen] = useState(false);

    const handleNavOpen = () => {
        setIsNavOpen(true);
    };

    const handleNavClose = () => {
        setIsNavOpen(false);
    };

    return (
        <>
            <header style={{ width: '100%', padding: '10px', backgroundColor: '#f8f9fa', textAlign: 'center', display: 'flex', alignItems: 'center' }}>
                <HamburgerMenuButton onClick={handleNavOpen} />
                <img src="/logo192.png" alt="Logo" className="spinning-logo" style={{ marginRight: '10px' }} />
                <h1>Date and Time Display</h1>
            </header>
            <SideNavBar isOpen={isNavOpen} onClose={handleNavClose} />
        </>
    );
};

export default Header;
