import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import DateDisplay from '../components/DateDisplay';

const Contact: React.FC = () => {
    const [formData, setFormData] = useState({
        name: '',
        email: '',
        message: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        console.log('Form Data:', formData); // Log form data for debugging
        emailjs.send(
            'service_u4wsgbr', // Your EmailJS service ID
            'template_5fb55os', // Your EmailJS template ID
            {
                from_name: formData.name,
                from_email: formData.email,
                message: formData.message
            },
            'HcluKvNoKzjbn4YPS' // Your EmailJS user ID
        ).then((response) => {
            console.log('EmailJS Response:', response); // Log response for debugging
            alert('Message sent successfully!');
        }).catch((err) => {
            console.error('EmailJS Error:', err); // Log error for debugging
            alert('Failed to send the message. Please try again.');
        });
    };

    return (
        <div className="container">
            <Header />
            <div className="content">
                <DateDisplay />
                <h2 style={{ fontSize: '3em', padding: '0px', textAlign: 'center' }}>Let's connect.</h2>
                <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: 'auto' }}>
                    <div>
                        <label>Name:</label>
                        <input
                            type="text"
                            name="name"
                            value={formData.name}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            name="email"
                            value={formData.email}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <div>
                        <label>Message:</label>
                        <textarea
                            name="message"
                            value={formData.message}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#007BFF', color: '#fff', border: 'none', cursor: 'pointer' }}>
                        Send Message
                    </button>
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default Contact;
