import React from 'react'
import { useNavigate } from 'react-router-dom'
import { ROUTES } from '../resources/routes-constants'
import { Helmet } from 'react-helmet';
import '../styles/_notfoundpage.sass';

const NotFoundPage: React.FC = () => {
    const navigate = useNavigate()

    /**
     * Call this function to redirect the user to the homepage.
     */
    const redirectToHomePage = () => {
        navigate(ROUTES.HOMEPAGE_ROUTE)
    }

    return (
        <div style={{ 
            position: 'relative', 
            width: '100%', 
            height: '100vh', // Ensure the container takes up the full height of the viewport
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center', 
            flexDirection: 'column' 
        }}>
            <Helmet>
                <title>Page Not Found</title>
            </Helmet>
            <h1 style={{ fontSize: '4em', color: 'red' }}>Oops 404!</h1>
            <p>Page Not Found</p>
            <span style={{ cursor: 'pointer', color: 'blue', textDecoration: 'underline' }} onClick={redirectToHomePage}>
                Click here to go back to the homepage.
            </span>
        </div>
    );
};

export default NotFoundPage;
