import React, { useState } from 'react';
import emailjs from 'emailjs-com';
import Header from '../components/Header/Header';
import Footer from '../components/Footer/Footer';
import DateDisplay from '../components/DateDisplay';

const Contact: React.FC = () => {
    const [formData, setFormData] = useState({
        from_name: '',
        reply_to: '',
        message: ''
    });

    const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
        setFormData({ ...formData, [e.target.name]: e.target.value });
    };

    const handleSubmit = (e: React.FormEvent) => {
        e.preventDefault();
        emailjs.send(
            'service_u4wsgbr',
            'template_teh2jol',
            formData,
            'HcluKvNoKzjbn4YPS'
        ).then(() => { // Remove 'response' since it is unused
            alert('Message sent successfully!');
        }).catch(() => { // Remove 'err' since it is unused
            alert('Failed to send the message. Please try again.');
        });
    };

    return (
        <div className="container">
            <Header />
            <div className="content">
                <DateDisplay />
                <h2 style={{ fontSize: '3em', padding: '0px', textAlign: 'center' }}>Contact</h2>
                <form onSubmit={handleSubmit} style={{ maxWidth: '600px', margin: 'auto' }}>
                    <div>
                        <label>Name:</label>
                        <input
                            type="text"
                            name="from_name"
                            value={formData.from_name}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <div>
                        <label>Email:</label>
                        <input
                            type="email"
                            name="reply_to"
                            value={formData.reply_to}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <div>
                        <label>Message:</label>
                        <textarea
                            name="message"
                            value={formData.message}
                            onChange={handleChange}
                            required
                            style={{ width: '100%', padding: '10px', margin: '10px 0' }}
                        />
                    </div>
                    <button type="submit" style={{ padding: '10px 20px', backgroundColor: '#007BFF', color: '#fff', border: 'none', cursor: 'pointer' }}>
                        Send Message
                    </button>
                </form>
            </div>
            <Footer />
        </div>
    );
};

export default Contact;
